mapper.select:
select ${groupType} groupType, sum(item.number) totalNumber, sum(item.amount) totalAmount from orderbill bill join orderbillitem item on bill.id = item.bill_id join employee e on e.id = bill.inputUser_id join product p on p.id = item.product_id join supplier s on s.id = bill.supplier_id where bill.status = 2
and bill.vdate >= #{beginDate}
and bill.vdate <= #{endDate}
and (p.name like concat('%', #{keyword},'%') or p.sn like concat('%', #{keyword},'%'))
and bill.supplier_id = #{supplierId}
and p.brand_id = #{brandId}
group by groupType

select ${groupType} groupType, sum(sa.number) totalNumber, sum(sa.saleAmount) totalAmount, sum(sa.saleAmount - sa.costAmount) profit from saleaccount sa join employee e on sa.saleman_id = e.id join product p on p.id = sa.product_id join client c on c.id = sa.client_id
and sa.vdate >= #{beginDate}
and sa.vdate <= #{endDate}
and (p.name like concat('%', #{keyword},'%') or p.sn like concat('%', #{keyword},'%'))
and sa.client_id = #{clientId}
and p.brand_id = #{brandId}
group by groupType
