mapper.update:
update product set name = #{name}, sn = #{sn}, costPrice = #{costPrice}, salePrice = #{salePrice},
imagePath = #{imagePath},
intro = #{intro}, brand_id = #{brandId}, brandName = #{brandName} where id = #{id}

mapper.select:
select id, name, sn, costPrice, salePrice, imagePath, intro, brand_id, brandName
from product
where id = #{id}
